let argv = process.argv;
if (argv.length < 3) {
	console.error('Usage: read-package.json <file> [<fields> ...]');
	process.exit(1);
}

let file = argv[2];
let readJson = require('read-package-json');

readJson(file, function (er, data) {
	if (er) throw er;
	if (argv.length === 3) {
		console.log(data);
	} else {
		argv.slice(3).forEach(function (field) {
			field = field.split('.');
			let val = data;
			field.forEach(function (f) {
				val = val[f];
			});
			console.log(val);
		});
	}
});
