'use strict';
let chain = require('slide').chain;
let build = require('../../build.js');
let npm = require('../../npm.js');
let packageId = require('../../utils/package-id.js');

module.exports = function (top, buildpath, pkg, log, next) {
	log.silly('build', packageId(pkg));
	chain([
    [ build.linkStuff, pkg.package, pkg.path, npm.config.get('global'), true ],
    [ build.writeBuiltinConf, pkg.package, pkg.path ]
	], next);
};
