module.exports = getType;

function getType (st) {
	let types = [
		'Directory',
		'File',
		'SymbolicLink',
		'Link', // special for hardlinks from tarballs
		'BlockDevice',
		'CharacterDevice',
		'FIFO',
		'Socket'
	];
	let type;

	if (st.type && types.indexOf(st.type) !== -1) {
		st[st.type] = true;
		return st.type;
	}

	for (let i = 0, l = types.length; i < l; i++) {
		type = types[i];
		let is = st[type] || st['is' + type];
		if (typeof is === 'function') is = is.call(st);
		if (is) {
			st[type] = true;
			st.type = type;
			return type;
		}
	}

	return null;
}
