let pz = require('../promzard');

let path = require('path');
let file = path.resolve(__dirname, 'substack-input.js');
let buf = require('fs').readFileSync(file);
let ctx = { basename: path.basename(path.dirname(file)) };

pz.fromBuffer(buf, ctx, function (er, res) {
	if (er)
		throw er;
	console.error(JSON.stringify(res, null, 2));
});
