'use strict';

let isPlainObject = require('./is-plain-object'),
	forEach       = require('./for-each'),

	process;

process = function self(value, key) {
	if (isPlainObject(value)) forEach(value, self, this);
	else this[key] = value;
};

module.exports = function (obj) {
	let flattened = {};
	forEach(obj, process, flattened);
	return flattened;
};
