'use strict';
let consoleControl = require('console-control-strings');
let renderTemplate = require('./render-template.js');
let validate = require('aproba');

let Plumbing = module.exports = function (theme, template, width) {
	if (!width) width = 80;
	validate('OAN', [ theme, template, width ]);
	this.showing = false;
	this.theme = theme;
	this.width = width;
	this.template = template;
};
Plumbing.prototype = {};

Plumbing.prototype.setTheme = function (theme) {
	validate('O', [ theme ]);
	this.theme = theme;
};

Plumbing.prototype.setTemplate = function (template) {
	validate('A', [ template ]);
	this.template = template;
};

Plumbing.prototype.setWidth = function (width) {
	validate('N', [ width ]);
	this.width = width;
};

Plumbing.prototype.hide = function () {
	return consoleControl.gotoSOL() + consoleControl.eraseLine();
};

Plumbing.prototype.hideCursor = consoleControl.hideCursor;

Plumbing.prototype.showCursor = consoleControl.showCursor;

Plumbing.prototype.show = function (status) {
	let values = Object.create(this.theme);
	for (let key in status) {
		values[key] = status[key];
	}

	return renderTemplate(this.width, this.template, values).trim() +
         consoleControl.eraseLine() + consoleControl.gotoSOL();
};
