let mod_fs = require('fs');
let mod_verror = require('../lib/verror');

let filename = '/nonexistent';

mod_fs.stat(filename, function (err1) {
	let err2 = new mod_verror.VError(err1, 'failed to stat "%s"', filename);

	/* The following would normally be higher up the stack. */

	let err3 = new mod_verror.VError(err2, 'failed to handle request');
	console.log(err3.message);
	console.log(err3.stack);
});
