let errors = require('./errors');
let types = require('./types');

let Reader = require('./reader');
let Writer = require('./writer');


///--- Exports

module.exports = {

	Reader: Reader,

	Writer: Writer

};

for (let t in types) {
	if (types.hasOwnProperty(t))
		module.exports[t] = types[t];
}
for (let e in errors) {
	if (errors.hasOwnProperty(e))
		module.exports[e] = errors[e];
}
